    private void jButton_GuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_GuardarActionPerformed
        java.util.Date fechaInicioUtil = jDateChooser_fecha_inicio.getDate();
        java.util.Date fechaFinUtil = jDateChooser_fecha_fin.getDate();

        if (fechaInicioUtil != null && fechaFinUtil != null) {
            java.sql.Date fechaInicio = new java.sql.Date(fechaInicioUtil.getTime());
            java.sql.Date fechaFin = new java.sql.Date(fechaFinUtil.getTime());
            DaoReportes rep = new DaoReportes();
            rep.ReporteVentasConDetalles(fechaInicio, fechaFin);
            JOptionPane.showMessageDialog(null, "Reporte de ventas con detalles creado con Ã©xito");
        } else {
            JOptionPane.showMessageDialog(null, "Por favor, seleccione ambas fechas.");
        }
    }//GEN-LAST:event_jButton_GuardarActionPerformed


